let defs = '<clipPath id="clip-path"><rect x="25.32" y="26.59" width="59.34" height="59.34" fill="none"/></clipPath>';

(<any>String.prototype).replaceAll = function(search, replacement) {
	var target = this;
	return target.replace(new RegExp(search, 'g'), replacement);
};

config.nodeOptions.width = 192.96;
config.nodeOptions.height = 100.8;
config.nodeOptions.collapsible = true;
config.tipOverOptions.tipOverChildrenCount = 10;
config.nodeOptions.margin = new NodeMargin(); // the template has background shadow and we need to make connectors a little bit longer
config.nodeOptions.margin.top = -7;

// add definitions
config.onBeforeRender = (args: RenderEventArgs) => {
	args.paper.append(<any>Snap.parse(defs));
	args.paper.select('#clip-path').toDefs();
	return null;
};

// Primitive templates filling with data
config.onBoxRender = (args: RenderBoxEventArgs) => {
	var tpl = templateBack + templateFront.replaceAll('##style##', 'display: none');

	tpl = tpl.replaceAll('##info##', 'parentId: ' + args.node.parentId +'  id: ' + args.node.id)
			.replaceAll('##text##', args.node.data.text.toUpperCase())
			.replaceAll('##email##', args.node.data.text.toLowerCase().split(' ').join('.') + '@dot.com');

	if (args.node.data.foto) {
		tpl = tpl.replaceAll('##foto##', args.node.data.foto);
	}
	else {
		tpl = tpl.replaceAll('##foto##', unknownFoto);
	}

	// return the actual SVG code to be representing node box on screen
	return tpl;
};

// handle custom arrow click to turn the card
// animate the card, using snap svg
// we can use a CSS solution also if we don;t want to use Snap.svg
config.onCustomClick = (args: CustomClickEventArgs) => {
	var id = OrgChartConfig.getFullNodeSelectorId(args.node);
	var boxNode = Snap.select(id); // this is a group node wrapping whole box template
	var backCard = boxNode.select('.back-card'); // find a back card inside the box group
	var frontCard = boxNode.select('.front-card'); // find a front card inside the box group
	var duration = 100;

	if (args.customName === 'click-back') {
		var matrix = Snap.matrix().scale(0,1, config.nodeOptions.width / 2, 0);
		backCard.animate({transform: matrix}, duration, mina.easein, () => {
			frontCard.animate({transform: new Snap.Matrix()}, duration, mina.easein);
		});
	}
	else {
		var matrix = Snap.matrix().scale(0,1, config.nodeOptions.width / 2, 0);
		frontCard.animate({transform: matrix}, duration, mina.easein, () => {
			backCard.animate({transform: new Snap.Matrix()}, duration, mina.easein);
		});
	}
};

// init the actual chart
new OrgChartSvg(config);

